import React from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Agents from './pages/Agents';
import Upload from './pages/Upload';

export default function App() {
  const navigate = useNavigate();
  const location = useLocation();
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  // Style function for nav links
  const navLinkStyle = (path) => ({
    padding: '10px 14px',
    borderRadius: '8px',
    textDecoration: 'none',
    fontWeight: 500,
    background: location.pathname === path ? '#1e293b' : 'transparent',
    color: 'white',
    transition: 'all 0.2s ease',
  });

  return (
    <div style={{ fontFamily: 'Inter, system-ui, Arial', minHeight: '100vh', display: 'flex' }}>
      {/* Sidebar */}
      <aside
  style={{
    width: 240,
    background: "#0f172a",
    color: "white",
    padding: 16,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between", // keeps logout at bottom
    height: "100vh",                 // 👈 ensures full screen height
    boxSizing: "border-box",         // avoids overflow issues
  }}
>
  <div>
    <h2
      style={{
        marginTop: 0,
        marginBottom: 24,
        fontSize: 20,
        fontWeight: 600,
      }}
    >
      ⚡ Dashboard
    </h2>

    {/* Navigation */}
    <nav style={{ display: "grid", gap: 8 }}>
      <Link to="/" style={navLinkStyle("/")}>🏠 Home</Link>
      {user.role === "admin" && (
        <Link to="/agents" style={navLinkStyle("/agents")}>👥 Agents</Link>
      )}
      {user.role === "admin" && (
        <Link to="/upload" style={navLinkStyle("/upload")}>📂 Upload & Distribute</Link>
      )}
    </nav>
  </div>

  {/* Logout button */}
  <div>
    <button
      onClick={logout}
      style={{
        background: "#ef4444",
        color: "white",
        border: 0,
        padding: "10px 14px",
        borderRadius: 8,
        cursor: "pointer",
        fontWeight: 500,
        width: "100%", // full width button
        transition: "all 0.2s ease",
      }}
      onMouseOver={(e) => (e.currentTarget.style.background = "#dc2626")}
      onMouseOut={(e) => (e.currentTarget.style.background = "#ef4444")}
    >
      🚪 Logout
    </button>
  </div>
</aside>


      {/* Main content */}
      <main style={{ flex: 1, padding: 24, background: '#f8fafc' }}>
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/agents" element={<Agents />} />
          <Route path="/upload" element={<Upload />} />
        </Routes>
      </main>
    </div>
  );
}
