import React, { useEffect, useState } from 'react';
import api from '../api/axios';

export default function Dashboard() {
  const [agents, setAgents] = useState([]);
  const [leads, setLeads] = useState([]);
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  useEffect(() => {
    const load = async () => {
      try {
        if (user.role === 'admin') {
          const [a, l] = await Promise.all([api.get('/agents'), api.get('/leads')]);
          setAgents(a.data); setLeads(l.data);
        } else {
          const mine = await api.get('/leads/mine');
          setLeads(mine.data);
        }
      } catch (e) { console.error(e); }
    };
    load();
  }, []);

  return (
    <div>
      <h1>Welcome, {user.name}</h1>
      {user.role === 'admin' ? (
        <div style={{ display: 'grid', gap: 16, gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))' }}>
          <div style={{ background: 'white', padding: 16, borderRadius: 12 }}>
            <h3>Total Agents</h3>
            <div style={{ fontSize: 32, fontWeight: 700 }}>{agents.length}</div>
          </div>
          <div style={{ background: 'white', padding: 16, borderRadius: 12 }}>
            <h3>Total Leads</h3>
            <div style={{ fontSize: 32, fontWeight: 700 }}>{leads.length}</div>
          </div>
        </div>
      ) : (
        <div>
          <h3>Your Assigned Leads</h3>
          <table style={{ width: '100%', background: 'white', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ textAlign: 'left', padding: 8 }}>First Name</th>
                <th style={{ textAlign: 'left', padding: 8 }}>Phone</th>
                <th style={{ textAlign: 'left', padding: 8 }}>Notes</th>
              </tr>
            </thead>
            <tbody>
              {leads.map((l) => (
                <tr key={l._id} style={{ borderTop: '1px solid #e5e7eb' }}>
                  <td style={{ padding: 8 }}>{l.firstName}</td>
                  <td style={{ padding: 8 }}>{l.phone}</td>
                  <td style={{ padding: 8 }}>{l.notes}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
