import React from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Agents from './pages/Agents';
import Upload from './pages/Upload';

export default function App() {
  const navigate = useNavigate();
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  return (
    <div style={{ fontFamily: 'Inter, system-ui, Arial', minHeight: '100vh', display: 'flex' }}>
      <aside style={{ width: 240, background: '#0f172a', color: 'white', padding: 16, position: 'relative' }}>
        <h2 style={{ marginTop: 0 }}>Dashboard</h2>
        <nav style={{ display: 'grid', gap: 8 }}>
          <Link to="/" style={{ color: 'white' }}>Home</Link>
          {user.role === 'admin' && <Link to="/agents" style={{ color: 'white' }}>Agents</Link>}
          {user.role === 'admin' && <Link to="/upload" style={{ color: 'white' }}>Upload & Distribute</Link>}
        </nav>
        <div style={{ marginTop: 'auto', position: 'absolute', bottom: 16 }}>
          <button onClick={logout} style={{ background: '#ef4444', color: 'white', border: 0, padding: '8px 12px', borderRadius: 8 }}>Logout</button>
        </div>
      </aside>
      <main style={{ flex: 1, padding: 24, background: '#f8fafc' }}>
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/agents" element={<Agents />} />
          <Route path="/upload" element={<Upload />} />
        </Routes>
      </main>
    </div>
  );
}
